function ToDo({ text, category }: IToDo) {
const onClick = (event:React.MouseEvent<HTMLButtonElement>) => {
  console.log(event?.currentTarget.name)

};
return (
  <li>
    <span> {text} </span>
    {category !== "DOING" && <button name="DOING" onClick={onClick}>Doing</button>}
