import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { atom, useRecoilState,useRecoilValue,useSetRecoilState } from "recoil";

interface IForm {
  toDo:string;
}

interface IToDo {
  text : string;
  id: number;
  category: "TO_DO" | "DOING" | "DONE"; 
}

const toDoState = atom<IToDo[]>({
   key:"toDo", //key
   default: [], //기본값
})



function ToDoList() {
    const [toDos, setToDos] = useRecoilState(toDoState);
    // const value = useRecoilValue(toDoState);//atom에 접근하기
    // const modFn = useSetRecoilState(toDoState); //atom value변경
    const { register, handleSubmit, setValue } = useForm<IForm>();
    const handleVaild = ({toDo}:IForm) => {
        setToDos((oldToDos) => [{text : toDo, id:Date.now(), category:"TO_DO"},...oldToDos]) //함수를 넣으면, 리턴값이 state값이된다.
        setValue("toDo", ""); //data가 유효하다면 출력 후 비우기
    };
  return (
    <div>
      <h1>To Dos</h1>
      <hr />
      <form onSubmit={handleSubmit(handleVaild)}>
        <input {...register("toDo",{
            required:"Please write a To Do",
        })} 
        placeholder="Write a to do" />
        <button>Add</button>
      </form>
      <ul></ul>
    </div>
  );
}



export default ToDoList;
